mapper.update:
update coupon
set state = #{state},
update_time = #{updateTime}
where id = #{id}

mapper.select:
        select c.* from coupon c
        where merchant_id = #{merchantId} and c.discount_product_id = #{discountProductId}

            and c.state = #{state}

	            and c.update_time >= #{startTime}


	            and c.update_time <= #{endTime}




	            and c.create_time >= #{startTime}


	            and c.create_time <= #{endTime}
